// import statements
import React from 'react';
import PropTypes from 'prop-types'; // Optional: For prop type checking

// Component definition

const GeneralContainer = (props) => {

    // Destructure props for easier access
    const {number, children, title, className } = props;

    return (
        // here is the resin container code with title, margins, color
        <div className="flex gap-4 mb-6">
        <div className="text-4xl font-bold text-gray-700 mt-8">{number}</div>
        <div className="flex-1">
          <div className="h-8 pl-6"> {/* Space for overlapping title */}
            <h2 className="text-xl font-semibold mb-4 -mt-3 bg-white pr-2 inline-block -ml-6">
              {title}
            </h2>
          </div>
          <div className="w-[930px] border-solid border-2 border-black-600 rounded-lg shadow-sm bg-white relative">
            <div className="p-6 -mt-8">
              {children}
            </div>
          </div>
        </div>
      </div>
    );
}

export default GeneralContainer;
